

import React, { useState, useEffect, useRef } from 'react';
import type { User, GridCellState } from '../types';
import MineGrid from './MineGrid';

interface PredictorPageProps {
    user: User;
    onUpdateUser: (user: User) => void;
}

const GRID_SIZE = 25;
const PREDICTION_LIMIT = 15;
// IMPORTANT: Set your affiliate link in the Vercel Environment Variables.
// The variable name must be VITE_AFFILIATE_LINK
// @ts-ignore
const AFFILIATE_LINK = import.meta.env.VITE_AFFILIATE_LINK || 'https://1waff.com/?p=YOUR_CODE_HERE';

// Base64 encoded sound effects (Royalty-free)
const REVEAL_SOUND_B64 = 'data:audio/mpeg;base64,...';
const NEXT_SOUND_B64 = 'data:audio/mpeg;base64,--gmaqqqq----------------';

const PredictorPage: React.FC<PredictorPageProps> = ({ user, onUpdateUser }) => {
    const [gridState, setGridState] = useState<GridCellState[]>(() => Array(GRID_SIZE).fill('hidden'));
    const [bombs, setBombs] = useState<number[]>([]);
    const [revealedStars, setRevealedStars] = useState<number[]>([]);
    const [bombsRevealed, setBombsRevealed] = useState(false);
    const [isPredicting, setIsPredicting] = useState(false);
    const [minesCount, setMinesCount] = useState(3);
    const [predictionsLeft, setPredictionsLeft] = useState(PREDICTION_LIMIT - user.predictionCount);

    const revealSound = useRef<HTMLAudioElement | null>(null);
    const nextSound = useRef<HTMLAudioElement | null>(null);

    useEffect(() => {
        setPredictionsLeft(PREDICTION_LIMIT - user.predictionCount);
    }, [user.predictionCount]);

    useEffect(() => {
        revealSound.current = new Audio(REVEAL_SOUND_B64);
        nextSound.current = new Audio(NEXT_SOUND_B64);
        // Preload sounds
        revealSound.current.load();
        nextSound.current.load();
    }, []);

    const playSound = (sound: React.RefObject<HTMLAudioElement>) => {
        if (sound.current) {
            sound.current.currentTime = 0;
            sound.current.play().catch(e => console.error("Error playing sound:", e));
        }
    };
    
    const handlePredict = () => {
        if (predictionsLeft <= 0 || user.awaitingDeposit) {
            const updatedUser = { ...user, awaitingDeposit: true };
            onUpdateUser(updatedUser);
            return;
        }

        setIsPredicting(true);
        setBombsRevealed(false);
        setRevealedStars([]);
        setGridState(Array(GRID_SIZE).fill('hidden'));
        
        // Simulate prediction delay
        setTimeout(() => {
            const newBombs: number[] = [];
            while (newBombs.length < minesCount) {
                const randomIndex = Math.floor(Math.random() * GRID_SIZE);
                if (!newBombs.includes(randomIndex)) {
                    newBombs.push(randomIndex);
                }
            }
            setBombs(newBombs);

            const newGridState = Array(GRID_SIZE).fill('hidden') as GridCellState[];
            const safeCells: number[] = [];
            for (let i = 0; i < GRID_SIZE; i++) {
                if (!newBombs.includes(i)) {
                    safeCells.push(i);
                }
            }
            
            // Randomly pick a few stars to reveal initially
            const starsToShow: number[] = [];
            const shuffledSafeCells = safeCells.sort(() => 0.5 - Math.random());
            const countToShow = Math.min(shuffledSafeCells.length, 5); // show 5 stars
            for (let i = 0; i < countToShow; i++) {
                starsToShow.push(shuffledSafeCells[i]);
            }

            setRevealedStars(starsToShow);
            
            let revealIndex = 0;
            const interval = setInterval(() => {
                if (revealIndex < starsToShow.length) {
                    playSound(revealSound);
                    setGridState(prev => {
                        const updated = [...prev];
                        updated[starsToShow[revealIndex]] = 'star';
                        return updated;
                    });
                    revealIndex++;
                } else {
                    clearInterval(interval);
                    setIsPredicting(false);
                    const updatedUser = { ...user, predictionCount: user.predictionCount + 1 };
                    onUpdateUser(updatedUser);
                }
            }, 150); // Stagger reveal for effect

        }, 1500); // Prediction thinking time
    };

    const handleNext = () => {
        playSound(nextSound);
        setGridState(prevGrid => {
            const newGrid = [...prevGrid];
            if (!bombsRevealed) {
                bombs.forEach(index => newGrid[index] = 'bomb');
                setBombsRevealed(true);
            } else {
                setBombs([]);
                setRevealedStars([]);
                setBombsRevealed(false);
                return Array(GRID_SIZE).fill('hidden');
            }
            return newGrid;
        });
    };

    const handleDeposit = () => {
        window.open(AFFILIATE_LINK, '_blank');
    };

    if (user.awaitingDeposit || predictionsLeft <= 0) {
        return (
            <div className="text-center p-8 glassmorphic-card gradient-border rounded-2xl shadow-2xl max-w-lg mx-auto">
                <h3 className="text-2xl font-bold text-white mb-3 font-['Orbitron']">Prediction Limit Reached</h3>
                <p className="text-text-secondary mb-6">You've used all your free predictions. Please make a new deposit to get more.</p>
                <div className="bg-blue-900/40 border border-white/10 rounded-lg p-4 mb-6">
                    <p className="font-semibold text-white">Why do I need to deposit?</p>
                    <p className="text-sm text-gray-300 mt-1">This small deposit confirms you are an active user and helps us maintain the prediction service. You will receive <strong>{PREDICTION_LIMIT} more predictions</strong> after a successful deposit of at least $4.</p>
                </div>
                <button
                    onClick={handleDeposit}
                    className="w-full btn btn-primary"
                >
                    Deposit Now
                </button>
                <p className="text-xs text-gray-400 mt-4">After depositing, close and reopen the app or log out and log back in to refresh your status.</p>
            </div>
        );
    }

    return (
        <div className="w-full max-w-md mx-auto">
            <div className="mb-6 p-4 glassmorphic-card rounded-xl text-center">
                <p className="text-text-secondary">Predictions Left</p>
                <p className="text-4xl font-bold text-white font-['Orbitron'] tracking-wider">{predictionsLeft}</p>
                 <div className="w-full bg-black/30 rounded-full h-2.5 mt-3 overflow-hidden border border-white/10">
                    <div 
                        className="bg-gradient-to-r from-accent-cyan to-accent-magenta h-2.5 rounded-full transition-all duration-500" 
                        style={{ width: `${(predictionsLeft / PREDICTION_LIMIT) * 100}%` }}
                    ></div>
                </div>
            </div>

            <MineGrid gridState={gridState} />

            <div className="mt-6 flex gap-4">
                <button
                    onClick={handlePredict}
                    disabled={isPredicting || bombs.length > 0}
                    className="flex-1 btn btn-primary disabled:opacity-50"
                >
                     {isPredicting ? (
                        <div className="flex items-center justify-center">
                            <div className="w-5 h-5 border-2 border-dashed rounded-full animate-spin border-white mr-2"></div>
                            <span>Predicting...</span>
                        </div>
                    ) : (
                        'Predict'
                    )}
                </button>
                <button
                    onClick={handleNext}
                    disabled={isPredicting || bombs.length === 0}
                    className="flex-1 btn disabled:opacity-50"
                >
                    {bombsRevealed ? 'Reset' : 'Next'}
                </button>
            </div>
        </div>
    );
};

export default PredictorPage;
